function checks(as,bs,type=1){
    if(as!=""&&bs!=""){
        var datas=document.getElementsByName(bs);
        switch(type){
            case(1):
             for (var j=0;j<datas.length; j++){
                datas[j].checked = document.getElementById(as).checked ;
             }
            break;  
            case(2):
                for (var i=0;i<datas.length;i++){
                    datas[i].checked = !datas[i].checked  ;
                }
            break;
        }
    }
}
